import { expect } from 'chai';
import { Equim } from '../src/Equim';
import Jimp = require('jimp');

const imagePath = './res/equirectangularRED.jpg';
const imageBase64 = 'data:image/jpeg;base64,';
const imageBase64WithoutPrefix = '';

describe('Equim', function() {

    describe('read', function() {
        it('should return a Jimp image without error', function() {
            Equim.read(imagePath, function(err, image){
                expect(err).to.be.undefined;
                expect(image).to.be.an.instanceOf(Jimp);
            });
        });
    });

    describe('readBase64', function() {
        it('should return a Jimp image without error', function() {
            Equim.readBase64(imageBase64, function(err, image){
                expect(err).to.be.undefined;
                expect(image).to.be.an.instanceOf(Jimp);
            });
        });
    });

    describe('readBase64 without prefix', function() {
        it('should return a Jimp image without error', function() {
            Equim.readBase64(imageBase64WithoutPrefix, function(err, image){
                expect(err).to.be.undefined;
                expect(image).to.be.an.instanceOf(Jimp);
            });
        });
    })

});
